#!/bin/bash\n\n# Athens EHS System - Responsive Design Testing Script\n# This script helps validate responsive implementation across different viewports\n\necho \"ðŸš€ Athens EHS System - Responsive Design Testing\"\necho \"================================================\"\n\n# Check if required tools are available\ncommand -v npm >/dev/null 2>&1 || { echo \"âŒ npm is required but not installed. Aborting.\" >&2; exit 1; }\n\necho \"âœ… Starting responsive design validation...\"\n\n# Start the development server in background\necho \"ðŸ“¡ Starting development server...\"\ncd frontend\nnpm run dev &\nDEV_PID=$!\n\n# Wait for server to start\necho \"â³ Waiting for server to start...\"\nsleep 10\n\n# Test different viewport sizes\necho \"ðŸ“± Testing responsive breakpoints...\"\n\n# Mobile viewport test\necho \"Testing Mobile (375x667)...\"\ncurl -s -o /dev/null -w \"%{http_code}\" http://localhost:5173 > /tmp/mobile_test.log\n\n# Tablet viewport test  \necho \"Testing Tablet (768x1024)...\"\ncurl -s -o /dev/null -w \"%{http_code}\" http://localhost:5173 > /tmp/tablet_test.log\n\n# Desktop viewport test\necho \"Testing Desktop (1920x1080)...\"\ncurl -s -o /dev/null -w \"%{http_code}\" http://localhost:5173 > /tmp/desktop_test.log\n\n# Check CSS compilation\necho \"ðŸŽ¨ Checking CSS compilation...\"\nif [ -f \"src/common/styles/responsive.css\" ]; then\n    echo \"âœ… Responsive CSS file exists\"\nelse\n    echo \"âŒ Responsive CSS file missing\"\nfi\n\n# Check component imports\necho \"ðŸ” Checking responsive component imports...\"\ngrep -r \"useResponsive\" src/ > /tmp/responsive_usage.log\ngrep -r \"ResponsiveFormWrapper\" src/ > /tmp/form_usage.log\n\nUSAGE_COUNT=$(wc -l < /tmp/responsive_usage.log)\nFORM_COUNT=$(wc -l < /tmp/form_usage.log)\n\necho \"ðŸ“Š Responsive hook usage: $USAGE_COUNT files\"\necho \"ðŸ“Š Responsive form usage: $FORM_COUNT files\"\n\n# Performance check\necho \"âš¡ Running basic performance check...\"\nif command -v lighthouse >/dev/null 2>&1; then\n    echo \"Running Lighthouse audit...\"\n    lighthouse http://localhost:5173 --only-categories=performance --chrome-flags=\"--headless\" --output=json --output-path=/tmp/lighthouse.json\n    PERF_SCORE=$(cat /tmp/lighthouse.json | grep '\"performance\"' -A 5 | grep '\"score\"' | cut -d':' -f2 | cut -d',' -f1 | tr -d ' ')\n    echo \"ðŸ“ˆ Performance Score: $PERF_SCORE\"\nelse\n    echo \"âš ï¸  Lighthouse not installed - skipping performance audit\"\nfi\n\n# Cleanup\necho \"ðŸ§¹ Cleaning up...\"\nkill $DEV_PID 2>/dev/null\nrm -f /tmp/*_test.log /tmp/responsive_usage.log /tmp/form_usage.log /tmp/lighthouse.json\n\necho \"âœ… Responsive design testing completed!\"\necho \"\"\necho \"ðŸ“‹ Next Steps:\"\necho \"1. Test manually in browser DevTools (F12 â†’ Device Toolbar)\"\necho \"2. Test on real devices if available\"\necho \"3. Check accessibility with screen readers\"\necho \"4. Validate touch interactions on mobile\"\necho \"5. Monitor performance metrics in production\"\necho \"\"\necho \"ðŸŽ¯ Target Metrics:\"\necho \"- Mobile Performance Score: >90\"\necho \"- First Contentful Paint: <2.5s\"\necho \"- Cumulative Layout Shift: <0.1\"\necho \"- Touch Target Size: â‰¥44px\"\n\ncd ..